static MYFLT
_clip(MYFLT x) {
    if (x < 0.0)
        return 0.0;
    else if (x >= 1.0)
        return 1.0;
    else
        return x;
}

static MYFLT
Sine_clip(MYFLT x) {
    if (x < 0) {
        x += ((int)(-x * ONE_OVER_512) + 1) * 512;
    }
    else if (x >= 512) {
        x -= (int)(x * ONE_OVER_512) * 512;
    }
    return x;
}

